targets.expr:
100 * (
  1 - (
    node_filesystem_avail_bytes{fstype!~"tmpfs|overlay", environment="$env", instance=~"$instance"}
    /
    on(instance, device)
    node_filesystem_size_bytes{fstype!~"tmpfs|overlay", environment="$env", instance=~"$instance"}
  )
)

topk(10,
  rate(
    mongodb_collstats_storageStats_totalSize{
      rs_state="1",
      database=~"$database",
      collection=~"$collection"
    }[15m]
  ) > 0
)
